sbcol.subsec:
The purpose of this section is to verify the consistency of a proposed axiom of infinity relative to the axioms of GST in HOL by proving that the axiom is true of the type GS.
(It is intended to be asserted of IND).
The axiom will then be asserted in a theory higher up the tree, and eventually the axiomatization of GST will be replaced by a conservative extension of that theory.

set_goal([], &qqtel;∃e: GS → GS → BOOL• ∀p• ∃q• (e p q ∧ (∀x• e x q ⇒ (
        (∃y• e y q ∧ ∀Z• ∃z• e z y ∧ ∀v• e v z ≡ e v x ∧ (Z v))
        ∧ (∀f•(∀u• e u x ⇒ e (f u) q) ⇒ ∃y• e y q ∧ ∀u• e u x ⇒ e (f u) y))))
&qqter;);
a (∃_tac &qqtel;$∈g&qqter; THEN strip_tac);
a (strip_asm_tac (∀_elim &qqtel;p&qqter; galaxies_∃_thm));
a (∃_tac &qqtel;g&qqter; THEN REPEAT strip_tac);
(* *** Goal "1" *** *)
a (∃_tac &qqtel;℘g x&qqter; THEN REPEAT strip_tac);
(* *** Goal "1.1" *** *)
a (all_fc_tac [get_spec &qqtel;galaxy&qqter;]);
(* *** Goal "1.2" *** *)
a (∃_tac &qqtel;Sep x Z&qqter;
	THEN rewrite_tac[gst_relext_clauses]
	THEN REPEAT strip_tac);
(* *** Goal "2" *** *)
a (∃_tac &qqtel;Imagep f x&qqter;
	THEN rewrite_tac[]
	THEN REPEAT strip_tac);
(* *** Goal "2.1" *** *)
a (lemma_tac &qqtel;Imagep f x ⊆g g&qqter;
	THEN1 (rewrite_tac [get_spec &qqtel;$⊆g&qqter;, get_spec &qqtel;Imagep&qqter;]
		THEN REPEAT strip_tac
		THEN asm_rewrite_tac[]
		THEN REPEAT (asm_fc_tac[])));
a (fc_tac[GImagepC]);
a (list_spec_nth_asm_tac 1 [&qqtel;x&qqter;, &qqtel;f&qqter;]);
(* *** Goal "2.2" *** *)
a (∃_tac &qqtel;u&qqter; THEN asm_rewrite_tac[]);
val strong_infinity_thm = save_pop_thm "strong_infinity_thm";

subsec.ft:
new_theory "surreal";
