subsec.ft:
birthdays_thm
&turnstil;
 	let	S = {L | Elems L ⊆ {i | 1 ≤ i ∧ i ≤ 365} ∧ # L = 23} 
 	in let	X = {L | L ∈ S ∧ ¬L ∈ Distinct}
 	in
 	 	S ∈ Finite
 	∧	¬#S = 0
 	∧	X ⊆ S
 	∧	#X / #S > 1/2

buffon_needle_thm
&turnstil;
 	let	S = {(θ, d) | θ ∈ ClosedInterval 0. π ∧ d ∈ ClosedInterval 0. 1.}
 	in let	x_axis = {(x, y) | y = 0.}
 	in let	needle(θ, d) =
 		{(x, y) | ∃t• t ∈ ClosedInterval 0. 1.
 			∧ x = t * Cos θ ∧ y = d - t * Sin θ}
 	in let	X = {(θ, d) | (θ, d) ∈ S ∧ ¬needle(θ, d) ∩ x_axis = {}}
 	in
 	 	X ⊆ S
 	∧	∃x s•
 	 	 	¬s = 0.
 	 	∧ 	X Area x
 	 	∧	S Area s
 	 	∧	x / s = 2. / π
